define([
	'jquery'
],function($){
	var loadingImg = "data:image/gif;base64,";

	var dropDownSelect = function(options){

		var maxWidth = options.maxWidth || 600;

		this.dropDownCallback = null;
		this.selectCallback = null;
		this.data = options.data || {};
		this.controlElement = $(options.selector);
		this.isShow = false;
		this.enabled = true;
		this.element = $("<div class='dropdown-pannel'></div>");
		this.element.hide();
		this.element.css({
			'maxWidth' : maxWidth,
			'minHeight' : this.controlElement.height(),
			'minWidth' : this.controlElement.width()
		});
		this.controlElement.after(this.element);
		this.budget = $('<span class="fa fa-chevron-down"></span>');
		this.controlElement.parent().css({
			"position" : "relative"
		});
		this.controlElement.css({
			"position":"relative"
		});
		this.budget.css({
			"marginLeft" : "10px"
		});

		this.label = this.controlElement.text();

		this.controlElement.empty();
		this.controlElement.append($("<span class='text'>"+this.label+"</span>"));
		this.controlElement.append(this.budget);

		var elemSelf = this;
		this.controlElement.click(function(e){
			if(elemSelf._click(e) && elemSelf.dropDownCallback){
				elemSelf.dropDownCallback.call(elemSelf,e,elemSelf);
			}
			e.stopPropagation();
		});
		$(document).click(function(){
			setTimeout(function(){
				elemSelf.hide();
			},100);
		});
		this.element.on("click","ul li",function(e){
			elemSelf.toggle();
			var value = $(this).data('value');
			elemSelf.controlElement.data('value',value);
			elemSelf.controlElement.find('span.text').text($(this).text());
			if(elemSelf.selectCallback){
				elemSelf.selectCallback.call(elemSelf,e,value);
			}
			e.stopPropagation();
		});

	}

	dropDownSelect.prototype = {
		trigger : function(){
			this.controlElement.trigger('click');
		},
		enable : function(){
			this.controlElement.removeClass("disable");
			this.enabled = true;
		},
		disable : function(){
			this.controlElement.addClass("disable");
			this.enabled = false;
			if(this.isShow){
				this.isShow = false;
				this.budget.removeClass("fa-chevron-up").addClass("fa-chevron-down");
				this.element.hide();
			}
		},
		getLabel : function (){
			return this.controlElement.find('span.text').text();
		},
		reset : function(){
			this.controlElement.find('span.text').text(this.label);
		},
		pending : function (){ this.isShow = true;
			this.budget.removeClass("fa-chevron-down").addClass("fa-chevron-up");
			this._show($('<img style="padding:10px 50px;" src="'+ loadingImg +'" />'));
		},
		setData : function(data){
			this.data = data;
			if(this._bindData() == 1){
				this.element.find('ul li').trigger('click');	
			}
		},
		hide : function(){
			this.isShow = false;
			this.budget.removeClass("fa-chevron-up").addClass("fa-chevron-down");
			this.element.hide();
			return false;
		},
		toggle : function(){
			if(this.isShow){
				this.hide();
			}else{
				this.isShow = true;
				this.budget.removeClass("fa-chevron-down").addClass("fa-chevron-up");
				var controlLeft = this.controlElement.position().left;
				var controlTop = this.controlElement.position().top;
				var controlHeight = this.controlElement.outerHeight(true);
				this.element.css({
					"left" : controlLeft,
					"top" : controlTop + controlHeight
				});
				this.element.show();
				return true;
			}
		},
		onDropdown : function(callback)
		{
			this.dropDownCallback = callback;	
		},
		onSelect : function(callback){
			this.selectCallback = callback;
		},
		_click : function(e){
			if(this.enabled){
				return this.toggle();
			}
			return false;
		},
		_bindData : function(){
			var ul = $('<ul class=""></ul>');
			var count = 0;
			for(var i in this.data){
				ul.append('<li data-value="'+i+'">'+this.data[i]+'</li>');
				count ++;
			}
			this._show(ul);
			return count;
		},
		_show : function(ele){
			var controlLeft = this.controlElement.position().left;
			var controlTop = this.controlElement.position().top;
			var controlHeight = this.controlElement.outerHeight(true);
			this.element.css({
				"left" : controlLeft,
				"top" : controlTop + controlHeight
			});
			this.element.empty();
			this.element.append(ele);
		}
	};

	return {
		create : function(options){
			return new dropDownSelect(options);
		}
	};
});
